import { FakeOptions, RandomInRangeOptions } from './core/core';
export interface RandomFloatOptions extends RandomInRangeOptions, FakeOptions {
}
/**
 * Generate a random random float.
 *
 * @category general
 *
 * @example
 *
 * randFloat()
 *
 * @example
 *
 * randFloat({ length: 10 })
 *
 */
export declare function randFloat<Options extends RandomFloatOptions = never>(options?: Options): [Options] extends [never] ? number : Options["length"] extends number ? number[] : number;
